mapper.insert:
INSERT INTO member (
me_email, me_pw, me_name, me_nick, me_birth
, me_regdate, me_status, me_grade, me_keyword, me_point
, me_zipcode, me_add1, me_add2
)
VALUES (
#{me_email}, #{me_pw}, #{me_name}, #{me_nick}, #{me_birth}
, sysdate, 0, 0, 'test', 0
, #{me_zipcode}, #{me_add1}, #{me_add2}
)

mapper.select:

SELECT me_email, me_pw, me_name, me_nick
FROM member 
WHERE me_email=#{me_email} AND me_pw=#{me_pw}
